import { JSDOM } from 'jsdom';
import { writeFile } from 'fs/promises';
import lang from 'language-classifier';

const start = data => {
	data.book.forEach(l => {
		l.parts.forEach(r => {
			r.content.replace('<body>', '');
			r.content.replace('</body>', '');
			r.content.replace('<head>', '');
			r.content.replace('</head>', '');
			r.content.replace('<h2>', '<h3>');
			r.content.replace('</h2>', '</h3>');
			r.content.replace('href="', 'href="https://developer.mozilla.org');

			const dom = new JSDOM(`<div class="astasta">${r.content}</div>`);
			const document = dom.window.document;

			document.querySelectorAll('.prev-next').forEach(el => {
				el.remove();
			});

			document.querySelectorAll('iframe').forEach(el => {
				el.remove();
			});

			document.querySelectorAll('img').forEach(el => {
				el.remove();
			});

			document.querySelectorAll('.metadata').forEach(el => {
				el.remove();
			});
			document.querySelectorAll('.table-container').forEach(el => {
				el.remove();
			});
			document.querySelectorAll('#смотрите_также').forEach(el => {
				el.parentElement.remove();
			});
			document.querySelectorAll('a').forEach(el => {
				el.removeAttribute('href');
			});
			document.querySelectorAll('.sample-code-frame').forEach(el => {
				el.parentElement.remove();
			});

			// Находим тег <pre>
			var preElement = document.querySelectorAll('pre');

			preElement.forEach(el => {
				const language = ['javascript', 'css', 'html'];
				const languageElement = el.parentNode.querySelector('.language-name');
				const headerElement = el.parentNode.querySelector('.language-name');

				const content = el.innerHTML;
				el.removeAttribute('class');
				el.removeAttribute('data-signature');
				el.setAttribute('compile', 'false');
				el.setAttribute('disable', 'true');
				el.setAttribute(
					'language',
					transformNonAviableLang(
						languageElement
							? languageElement.textContent
							: language[lang(el.textContent)]
							? language[lang(el.textContent)]
							: 'html'
					)
				);
				content.replace('<code>', '');
				content.replace('</code>', '');

				el.innerHTML = el.textContent;
				if (headerElement) {
					headerElement.remove();
				}
			});
			r.content = document.querySelector('.astasta').innerHTML;
		});
	});

	writeFile('./data.ts', JSON.stringify(data));
};

const transformNonAviableLang = lang => {
	if (lang == 'js') {
		return 'js';
	}
	if (lang == 'html') {
		return 'markup';
	}
	return lang;
};

